            acoes: acoesSugeridas,
            comandos: [], // Vazio para versÃ£o simplificada
            manutencao: {
                horasBomba: Math.round(diasSemLimpeza * 0.5), // Estimado
                ciclosValvula: Math.round(diasSemLimpeza * 2), // Estimado
                diasSemLimpeza: diasSemLimpeza,
                volumeDescartado: 0 // Para versÃ£o simplificada
            },
            timestamp: timestamp,
            // Indicadores para dashboard
            indicadores: {
                nivelOK: nivel > 15,
                phOK: ph >= 6.5 && ph <= 8.0,
                turbidezOK: turbidez <= 5,
                autonomiaOK: autonomia > 3,
                sistemaOK: sistemaAtivo
            },
            // Contadores para estatÃ­sticas
            contadores: {
                totalAlertas: alertas.length,
                totalAvisos: avisos.length,
                totalAcoes: acoesSugeridas.length
            }
        },
        topic: "sistema_completo"
    }
];

// Recebe os dados processados do sistema
var data = msg.payload;



// Formata os dados para a API C# conforme a estrutura esperada
var apiPayload = {
    "IdDevice": data.IdDevice || 2,
    "ReadingDatetime": data.ReadingDatetime,
    "LevelPct": parseFloat(data.LevelPct) || 0,
    "TurbidityNtu": parseFloat(data.TurbidityNtu) || 0,
    "PhLevel": parseFloat(data.PhLevel) || 7.0
};

// Log para debug
node.log("Enviando dados para API: " + JSON.stringify(apiPayload));

// Prepara mensagem para o HTTP request
msg.payload = apiPayload;
msg.headers = {
    "Content-Type": "application/json",
    "Accept": "application/json"
};

return msg;
